#include <stdio.h>
#include <stdint.h>

//register addresses:
#define RCC_BASE 0x40021000
#define PORTA_BASE 0x40010800
#define RCC_APB2ENR *((volatile uint32_t*)(RCC_BASE+0X18))
#define PORTA_CRH   *((volatile uint32_t*)(PORTA_BASE+0X04))
#define PORTA_ODR   *((volatile uint32_t*)(PORTA_BASE+0X0C))

#define RCC_CFGR *((volatile unsigned int*)(RCC_BASE+0x4))

void init()
{
	RCC_CFGR |= (1<<10); //set APB1 to be divided by 2

	RCC_CFGR |= (1<<11);
	RCC_CFGR |= (1<<13); //set APB2 to be divided by 4
}

int main(void)
{
	init();
	volatile int i;
	RCC_APB2ENR |= (1<<2);
	PORTA_CRH &= 0Xff0fffff;
	PORTA_CRH |= 0X00200000;
	while(1)
	{
		PORTA_ODR |= (1<<13);
		for(i=0;i<5000;i++);
		PORTA_ODR &= ~(1<<13);
		for(i=0;i<5000;i++);
	}
	return 0;
}
